import sqlite3# sozdat/podkluchitsa k bazeconnection = sqlite3.connect('dostavka.db')# sozdaem perevodchiksql = connection.cursor()# zapros na sozdanie tablici# ql.execute('CREATE TABLE users (id INTEGER, name TEXT, phone_number TEXT, loc_lat REAL, loc_long REAL, gender TEXT);')# dobavlenie polzovatelyadef add_user(user_id, name, phone_number, latitude, longitude,gender):    # sozdat/podkluchitsa k baze    connection = sqlite3.connect('dostavka.db')    # sozdaem perevodchik    sql = connection.cursor()    # dobavlenie polzovatelya v bazu    sql.execute('INSERT INTO users VALUES (?, ?, ?, ?, ?, ?);', (user_id, str(name), str(phone_number), latitude, longitude, str(gender)))    # fiksiruem obnovleniya    connection.commit()# poluchenie polzovatelyadef get_users():    # sozdat/podkluchitsa k baze    connection = sqlite3.connect('dostavka.db')    # sozdaem perevodchik    sql = connection.cursor()    # zapros dlya polucheniya dannix iz bazi    users = sql.execute('SELECT name, id, gender FROM users;')    # вывод в виде списка с кортежами    return users.fetchall()# zapros dlya udaleniya iz bazidef delete_user():    # sozdat/podkluchitsa k baze    connection = sqlite3.connect('dostavka.db')    # sozdaem perevodchik    sql = connection.cursor()    # otpravlyaem zapros na udalenie    sql.execute('DELETE FROM users')    # зафиксировать обновления    connection.commit()delete_user()